import React from "react"
import {
  Card,
  CardHeader,
  CardTitle,
  CardBody,
  Row,
  Col,
  FormGroup,
  // Form,
  Input,
  Button,
  Label
} from "reactstrap"
import fgImg from "../../../assets/img/pages/forgot-password.png"
import { history } from "../../../history"
import "../../../assets/scss/pages/authentication.scss"
import axios from 'axios';
import {Alert} from "reactstrap";

class ForgotPassword extends React.Component {
  state = {
    email: "",
    error : "",
    status : false,
  }
  callApi(){

    console.log('calling api');
    const {email} = this.state;
    if(email !== ""){
    const base_url = "http://localhost:2000";
     axios
      .post(base_url + "/auth/forget-password-email", {
        email: email,
        login_type : 'email',
      })
      .then(response => {
        if (response.data) {
          this.setState({status : response.data.status})
          history.push("/reset-password");
        }
      })
      .catch(err => {

        if(err.response && err.response.data){
          console.log( err.response.data.error)
          this.setState({error : err.response.data.error});
        }else{
          this.setState({error : "Something went wrong"});
        }
      })
    }else{
      this.setState({error : "Enter valid Email"});
    }
  }

  render() {
    const {email, error} =  this.state;

        return (
      <Row className="m-0 justify-content-center">
        <Col
          sm="8"
          xl="7"
          lg="10"
          md="8"
          className="d-flex justify-content-center"
        >
          <Card className="bg-authentication rounded-0 mb-0 w-100">
            <Row className="m-0">
              <Col
                lg="6"
                className="d-lg-block d-none text-center align-self-center"
              >
                <img src={fgImg} alt="fgImg" />
              </Col>
              <Col lg="6" md="12" className="p-0">
                <Card className="rounded-0 mb-0 px-2 py-1">
                  <CardHeader className="pb-1">
                    <CardTitle>
                      <h4 className="mb-0">Recover your password</h4>
                    </CardTitle>
                  </CardHeader>
                 {error === "" && <p className="px-2 auth-title">
                    Please enter your email address and we'll send you
                    instructions on how to reset your password.
                  </p>}
                  {error !== "" &&  <Alert color="warning">{error}</Alert>}
                  <CardBody className="pt-1 pb-0">
                    {/* <Form > */}
                      <FormGroup className="form-label-group">
                        <Input type="text" name="email" placeholder="Email" required value={email} onChange={e => this.setState({ email: e.target.value })}/>
                        <Label>Email</Label>
                      </FormGroup>
                      <div className="float-md-left d-block mb-1">
                        <Button.Ripple
                          color="primary"
                          outline
                          className="px-75 btn-block"
                          onClick={() => history.push("/login")}
                        >
                          Back to Login
                        </Button.Ripple>
                      </div>
                      <div className="float-md-right d-block mb-1">
                        <Button.Ripple
                          color="primary"
                          type="submit"
                          className="px-75 btn-block"
                          onClick={() => this.callApi()}
                        >
                          Recover Password
                        </Button.Ripple>
                      </div>
                    {/* </Form> */}
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </Card>
        </Col>
      </Row>
    )
  }
}
export default ForgotPassword
